import './App.css';
import {useState,useEffect} from 'react'
import axios from 'axios'
import {MenuList} from './MenuList';
import { OrdersList } from './OrdersList';

const App = ()=>{
  const [menus,setMenus] = useState([])
  const [orders,setOrders] = useState([])
  const [loadOrders,setLoadOrders] = useState(false)

  useEffect(()=>{
    Promise.all([axios.get('http://localhost:3030/menu-list'),
                 axios.get('http://localhost:3030/order-list')])
      .then((response)=>{
          const [menu,order] = response
          setMenus(menu.data)
          setOrders(order.data)
          if(order.data.length>0){
            setLoadOrders(true)
          }
      })
      .catch((error)=>{
          console.log('Error in getting data in App',error)
      })
    },[])


    function updateOrder(id,name){

      axios.put(`http://localhost:3030/order/${id}`,{isCompleted : true})
      .then((response)=>{
        console.log('response',response.data)
          setOrders(response.data)
      })
      .catch((error)=>{
          console.log('Error in update order function',error)
      })
    }

    function handleAddOrders(id){
      const item = {
        menuItem : id,
        isCompleted : false
      }
      axios.post('http://localhost:3030/add-order',item)
      .then((response)=>{
        console.log('response',response.data)
          setOrders([...orders,item])
      })
      .catch((error)=>{
        console.log('Error during adding',error)
      })
    }

  return (
    <div className='container'>
       <h1 className='text-center'>Cafe Order Management</h1><br/><br/>
       <div className='row'>
            <MenuList menus={menus} handleAddOrders={handleAddOrders} />
            {(loadOrders) && <OrdersList orders={orders} menus={menus} updateOrder={updateOrder}/>}
        </div>
    </div>
  )
}

export default App;
