import React from "react"
import { MenuName } from "./helper/utility"

const {useState} = React

export const OrdersList = (props)=>{
    const {orders,menus,updateOrder} = props
    const [textStatus,setTextStatus] = useState('')
    const [checked,setChecked] = useState(false)

    function handleDone(id){
        setChecked(true)
        updateOrder(id)
        setTextStatus(MenuName(menus,id))
    }

    return(
            <div className='col-6'>
                <h3>Orders {(textStatus.length>0) && <span className="float-end">The {textStatus} order is ready !</span>}</h3>
                <div className="row">
                {(orders.length>0) &&
                   (orders.map((order,i)=>{
                    console.log(order.backgroundColor)
                    return(
                        //style={order.backgroundColor}
                        <div className='card me-3 p-0' style={{width:'fit-content'}} key={order._id}>
                                <div className='card-body'>
                                    <h5>#{i+1}{(i===0) && (<input type='checkbox' className="float-end" value={checked} onChange={handleDone(order._id)}  />)} </h5>
                                    <p>{MenuName(menus,order.menuItem)}</p>
                                </div>
                        </div>
                        )
                    }))
                }
                </div>
        </div>
    )
}
