import { useState } from "react"
import { menuName } from "./helper/utility"

export const OrderCard = (props)=>{
    const {order,i,updateOrders,nextOrder,menus} = props
    const [checked,setChecked] = useState(false)
    const [backgroundColor,setBackgroundColor] = useState((order.isCompleted) ? {background : '#59af59'} :  {background : '#f59149'} )

    function handleChange(e){
        setChecked(e.target.checked)
        updateOrders(order._id,order.menuItem)
    }

    function handleNext(){
        nextOrder()
        setChecked(!checked)
    }

    return (
        <div style={{width:'fit-content'}}>
            <div className='card me-3 mt-3 p-0' style={backgroundColor}>
                <div className='card-body'>
                    <h5>#{i+1}{((i===0) && (<input type='checkbox' className="float-end" checked={ordered} onChange={handleChange}/>)) ||
                        ((i===1) && (<button type="button" className="btn btn-link text-white" onClick={handleNext}>Next</button>))} </h5>
                        <p>{menuName(menus,order.menuItem)}</p>
                 </div>
            </div>
        </div>
    )
}
