export const OrdersList = (props)=>{
    const {orders,menus,updateOrder} = props
                const [backgroundColor,setBackgroundColor] = useState({background : '#f59149'})
                const [textStatus,setTextStatus] = useState('')

                function handleDone(id,name){
                    console.log(name)
                    updateOrder(id)
                    setBackgroundColor({background:'#59af59'})
                    setTextStatus(name)
                }

                return(
                    <div className='col'>
                        <h3>Orders {(textStatus.length>0) && <span className="float-end">The {textStatus} order is ready !</span>}</h3>
                        {
                            orders.map((order,i)=>{
                                return(
                                    <div className='card' style={{width:'fit-content'}} key={order._id}>
                                        <div className='card-body' style={backgroundColor}>
                                            <h5>#{i+1}{(!order.isCompleted) && <button type="button"  className="btn btn-link" onClick={()=>{handleDone(order._id,menus.find(menu=>menu._id==order.menuItem).name)}}>Done</button>}</h5>
                                            <p>{menus.find(menu=>menu._id==order.menuItem).name}</p>
                                        </div>
                                    </div>
                                )
                            })
                        }
                    </div>
                )
}
